= 1 + 1 = 2\n    # 当 n = 4的时候， h = f(3) + f(2) = 3 + 1 = 4\n    def Fibonacci2(self, n):\n        if n == 0:\n            return 0;\n        if n == 1:\n            return 1;\n\n        ret = 0\n        a = 1\n        b = 0\n        for i in range(0, n-1):\n            ret = a + b\n            b = a\n            a = ret\n        return ret\n\nif __name__ == \'__main__\':\n    print(Solution().Fibonacci2(10))\n</code></pre>\n, tagUid=5c4c541e600ff422ccb371ee788f59d6, blogSortUid=718c76d4a84e408da23e04949a75e7e8, fileUid=d134bdfe255b9aa8c2ce44da8297f6a6, adminUid=1f01cd1d2f474743b241d74008b12333, isPublish=0, isOriginal=1, author=MadLife, articlesPart=蘑菇博客, level=0, tagList=[Tag(content=测试标签, clickCount=2, sort=12)], photoList=[http://localhost:8600//blog/admin/jpg/2020/2/1/1580528781247.jpg], blogSort=BlogSort(sortName=测试3, content=测试3, clickCount=0, sort=1), praiseCount=null, copyright=null, levelKeyword=null, useSort=0, sort=0, startComment=1)]}','删除选中博客',1,'2020-04-28 09:19:20','2020-04-28 09:19:20','XX|XX|内网IP|内网IP',206),('94c43b62abdbc2cc995a684721b36d57','admin','1f01cd1d2f474743b241d74008b12333','192.168.80.1','/systemConfig/editSystemConfig','POST','com.moxi.mogublog.admin.restapi.SystemConfigRestApi','editSystemConfig','{\"systemConfigVO\":SystemConfigVO(qiNiuAccessKey=, qiNiuSecretKey=, qiNiuBucket=, qiNiuArea=, uploadQiNiu=0, uploadLocal=1, picturePriority=0, localPictureBaseUrl=http://localhost:8600/, qiNiuPictureBaseUrl=, email=123456789@qq.com, emailUserName=null, emailPassword=, smtpAddress=null, smtpPort=, startEmailNotification=0)}','修改系统配置',1,'2020-05-30 09:09:13','2020-05-30 09:09:13','XX|XX|内网IP|内网IP',151),('9511165e66774799a6b9c89d29efd61e','admin','1f01cd1d2f474743b241d74008b12333','192.168.80.1','/role/edit','POST','com.moxi.mogublog.admin.restapi.RoleRestApi','update','{\"result\":org.springframework.validation.BeanPropertyBindingResult: 0 errors,\"roleVO\":RoleVO(roleName=一般管理员, summary=一般管理员, categoryMenuUids=[\"49b42250abcb47ff876bad699cf34