if n == 0:\n            return 0;\n        if n == 1:\n            return 1;\n\n        ret = 0\n        a = 1\n        b = 0\n        for i in range(0, n-1):\n            ret = a + b\n            b = a\n            a = ret\n        return ret\n\nif __name__ == \'__main__\':\n    print(Solution().Fibonacci2(10))\n</code></pre>\n, tagUid=5c4c541e600ff422ccb371ee788f59d6, blogSortUid=718c76d4a84e408da23e04949a75e7e8, fileUid=d134bdfe255b9aa8c2ce44da8297f6a6, adminUid=1f01cd1d2f474743b241d74008b12333, isPublish=1, isOriginal=1, author=MadLife, articlesPart=蘑菇博客, level=0, tagList=[Tag(content=测试标签, clickCount=2, sort=12)], photoList=[http://localhost:8600//blog/admin/jpg/2020/2/1/1580528781247.jpg], blogSort=BlogSort(sortName=测试3, content=测试3, clickCount=0, sort=1), praiseCount=null, copyright=null, levelKeyword=null, useSort=0, sort=0, startComment=1)]}','删除选中博客',1,'2020-04-28 09:18:14','2020-04-28 09:18:14','XX|XX|内网IP|内网IP',230),('f35144b26a06e2031eff1a30b51cb7d0','admin','1f01cd1d2f474743b241d74008b12333','192.168.80.1','/picture/setCover','POST','com.moxi.mogublog.admin.restapi.PictureRestApi','setCover','{\"result\":org.springframework.validation.BeanPropertyBindingResult: 0 errors,\"pictureVO\":PictureVO(fileUid=null, fileUids=null, picName=null, pictureSortUid=976dbeb57cb6d5234c1c73e1de8522cf)}','通过图片Uid将图片设为封面',1,'2020-04-07 15:45:58','2020-04-07 15:45:58','XX|XX|内网IP|内网IP',144),('f37fd41a9b46dfdeb74137281154eec6','admin','1f01cd1d2f474743b241d74008b12333','192.168.80.1','/blog/edit','POST','com.moxi.mogublog.admin.restapi.BlogRestApi','edit','{\"result\":org.springframework.validation.BeanPropertyBindingResult: 0 errors,\"request\":SecurityContextHolderAwareRequestWrapper[ org.springframework.security.web.header.HeaderWriterFilter$HeaderWriterRequest@9974d9e],\"blogVO\":BlogVO(title=JAVA, summary=测试2, content=<p>测试2123</p>\n, tagUid=6b0ba63beabccc91c4f8fb938984f8a3,5c4c541e600ff422ccb371ee788f59d6,63379410b07ddb33337c0d51699555e1,7cd66e64cf9ee686d67573abac0aa4